mapper.update:

UPDATE USERS
SET    USER_PW  = #{userPw}
	   , MOD_ID = #{modId}
	   , MOD_DT = now() 
WHERE USER_SEQ = #{userSeq}

mapper.select:

SELECT COUNT (*) CNT
FROM USERS
WHERE email_addr = #{emailAddr}
	AND USER_SEQ != #{userSeq}

